pragma solidity ^0.5.2;

import {IERC20} from "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol"; // FIXME Establecer la ruta correcta
import "openzeppelin-solidity/contracts/ownership/Ownable.sol"; // FIXME Establecer la ruta correcta
import "openzeppelin-solidity/contracts/math/SafeMath.sol"; // FIXME Establecer la ruta correcta

/**
 * Gestión de cursos, inscritos, asistencias y feedback
 */
contract Academy is Ownable {
    using SafeMath for uint256;

	IERC20 ierc20;

    address myTokenContract = 0xf96bEc259985175cF8aBa01dECDc8E04D5067fD3; // FIT Token (Formation Incentive Token)

    uint256 private defaultUserFund = 25000000; // TODO set() onlyOwner

    mapping (address => address) tutor;                                     // curso => tutor
    mapping (address => uint256) value;                                     // curso => valor en tokens
    mapping (address => bool) isUser;                                       // usuario => ¿está dado de alta?
    mapping (address => mapping (address => bool)) isSignedup;              // alumno => curso => ¿está inscrito?
    mapping (address => mapping (address => bool)) isAttendanceConfirmed;   // alumno => curso => ¿ha asistido?
    mapping (address => mapping (address => bool)) isFeedbackProvided;      // alumno => curso => ¿ha proporcionado feedback?
    mapping (address => mapping (address => uint256)) weightedValue;           // alumno => curso => valor tras feedback

    event TokenDistributionResolved(address course, address student);

	constructor(address _myTokenContract) public {
	    if (_myTokenContract != address(0))
	        myTokenContract = _myTokenContract;
		ierc20 = IERC20(myTokenContract);
	}    

	/**
	 * ¿Está este usuario dado de alta?
	 */ 
	modifier onlyAuthorised() {
	    if (!isUser[msg.sender])
	        revert("El usuario no está dado de alta");
        _;
	}
	/**
	 * ¿Es el tutor del curso?
	 */
	modifier onlyTutor(address course) {
	    if (tutor[course] != msg.sender)
	        revert("El usuario no es el tutor del curso");
        _;
	}
	/**
	 * ¿Está el alumno inscrito en el curso?
	 */
    modifier onlySignedupStudent(address course) {
        if (!isSignedup[msg.sender][course])
            revert("El alumno no está inscrito en el curso");
	    _;
	}

	/**
	 * Permite al owner del contrato transferir los fondos de éste a otra dirección¡
	 */
    function transfer(address recipient, uint256 amount) public onlyOwner returns (bool) {
        return ierc20.transferFrom(address(this), recipient, amount);
    }

	/**
	 * Da de alta un usuario en la Academia
	 */
	function addUser(address user) public onlyOwner returns (bool) {
	    _addUser(user, defaultUserFund);
	    return true;
	}
	function addUser(address user, uint256 initialFund) public onlyOwner returns (bool) {
	    _addUser(user, initialFund);
	    return true;
	}
	function _addUser(address user, uint256 fund) internal {
	    require(ierc20.transfer(user, fund), "La Academia está sin fondos");
		isUser[user] = true;
	}

	/**
     * Returns true if the address is the current User.
     */
    function actualUser(address maybeUser) public view returns (bool) {
        return isUser[maybeUser];
    }

    /**
     * Returns true if the address is the current User.
     */
    function getTokenAddress() public view returns (address) {
        return myTokenContract;
    }

	/**
	 * Crear curso. Cualquier usuario dado de alta puede crear un curso
	 */
	function createCourse(address course, uint256 _value) public onlyAuthorised {
	    require(course != address(0), "Identificador de curso inválido");
	    require(tutor[course] == address(0), "El curso ya existe");

        tutor[course] = msg.sender;
        value[course] = _value;
	}

	/**
	 * Cambiar tutor de curso
	 */
	function changeTutor(address course, address newTutor) public onlyTutor(course) {
	    require(newTutor != address(0), "La dirección del nuevo tutor no es válida"); 
	        tutor[course] = newTutor;
	}

	/**
	 * Inscripción en un curso. Cualquier usuario se pude inscribir
	 */
	function signup(address course) public onlyAuthorised {
	    require(ierc20.transferFrom(msg.sender, address(this), value[course]), "no ha sido posible realizar la transacción de tokens");
	    isSignedup[msg.sender][course] = true;
	}

	/**
	 * El tutor confirma la asistencia de un alumno
	 */
	function setAttendance(address course, address student) public onlyTutor(course) {
	    require(isSignedup[student][course], "El alumno no está inscrito en el curso");
		require(!isAttendanceConfirmed[student][course], "Ya se ha proporcionado información sobre la asistencia de esta dirección");

	    isAttendanceConfirmed[student][course] = true;
	    _resolveTokenDistribution(course, student);
	}

	/**
	 * Feedback del curso proporcionado por el alumno
	 */
	function provideFeedback(address course, uint256 feedback) public onlySignedupStudent(course) {
	    require((feedback <= 4) && (feedback >= 0), "Rango incorrecto. El feedback debe ser 0, 1, 2, 3 ó 4");
	    require(!isFeedbackProvided[msg.sender][course], "Ya se ha proporcionado feedback con esta dirección");

	    uint256 weight = feedback.mul(25);
	    weight = weight.mul(value[course]);
	    weightedValue[msg.sender][course] = weight.div(100);

	    isFeedbackProvided[msg.sender][course] = true;
	    _resolveTokenDistribution(course, msg.sender);
	}

	/**
	 * Resuelve el reparto de tokens
	 */
	function _resolveTokenDistribution(address course, address student) internal {
	    if(isAttendanceConfirmed[student][course] && isFeedbackProvided[student][course]){
			/**
			*  Resuelve el envío de tokens al tutor
			*/
			ierc20.transfer(tutor[course], weightedValue[student][course]);
			require((weightedValue[student][course]>0), "---weightedValue probably 0---");
			/**
			*  Resuelve el envío de tokens al alumno
			*/
			uint256 bonus = value[course].mul(25);
			bonus = bonus.div(100);
			ierc20.transfer(student, value[course].add(bonus));
			// ierc20.transferFrom(address(this), tutor[course], weightedValue[msg.sender][course]);

			emit TokenDistributionResolved(course, student);
		}
	}
}
